const express = require('express');
const cors = require('cors');
const app = express();
const mysql = require('mysql2');

app.use(cors());

app.get('/actor', (req, res) => {
    const pool = mysql.createPool({
        host: "localhost",
        user: "root",
        password: "0625",
        database: "sakila", 
        port: 3306,
        connectionLimit: 10
    });

    pool.query("SELECT * FROM actor LIMIT 20", function(err, results, fields) {
        if (err) {
            console.error("Error en la consulta a la base de datos:", err);
            res.status(500).json({ error: "Error en la consulta a la base de datos" });
        } else {
            console.log(results);
            res.json(results);
        }
    });
});

app.post('/actor', (req, res) => {
    res.json({ mensaje: "Server express respondiendo a post" });
});

app.delete('/actor', (req, res) => {
    res.json({ mensaje: "Server express respondiendo a delete" });
});

app.listen(8082, () => {
    console.log("Servidor Express corriendo en puerto 8082");
});
